'DECLARE',
'    v_temp_order_id NUMBER;',
'    v_total_price NUMBER(10, 2);',
'    v_commission_order NUMBER(10, 2);',
'    v_commission_rate CONSTANT NUMBER := 0.01; -- Prowizja wynosi 1%',
'BEGIN',
unistr('    -- Przetwarzanie ka\017Cdego unikalnego koszyka'),
'    FOR rec IN (SELECT DISTINCT basket_id, employee_id, order_date FROM basket_staging) LOOP',
unistr('        -- Obliczenie sumarycznej ceny zam\00F3wienia bez mno\017Cnika'),
'        SELECT ROUND(SUM(bs.quantity * p.price), 2) INTO v_total_price',
'        FROM basket_staging bs',
'        JOIN Products p ON bs.product_id = p.product_id',
'        WHERE bs.basket_id = rec.basket_id;',
'',
unistr('        -- Obliczenie prowizji dla zam\00F3wienia uwzgl\0119dniaj\0105c mno\017Cnik'),
'        SELECT ROUND(SUM(bs.quantity * p.price * c.multiplier * v_commission_rate), 2) INTO v_commission_order',
'        FROM basket_staging bs',
'        JOIN Products p ON bs.product_id = p.product_id',
'        JOIN CATEGORIES c ON p.category_id = c.category_id',
'        WHERE bs.basket_id = rec.basket_id;',
'',
unistr('        -- Wstawienie rekordu do tabeli TEMP_ORDERS z sumaryczn\0105 cen\0105 i prowizj\0105'),
'        INSERT INTO temp_orders (basket_id, employee_id, order_date, price_sum, commission_order)',
'        VALUES (rec.basket_id, rec.employee_id, rec.order_date, v_total_price, v_commission_order)',
'        RETURNING temp_order_id INTO v_temp_order_id;',
'',
unistr('        -- Wstawienie rekord\00F3w do tabeli ORDER_ITEMS_TEMP'),
'        INSERT INTO order_items_temp (temp_order_id, product_id, quantity, price)',
'        SELECT v_temp_order_id, bs.product_id, bs.quantity, p.price',
'        FROM basket_staging bs',
'        JOIN Products p ON bs.product_id = p.product_id',
'        WHERE bs.basket_id = rec.basket_id;',
'    END LOOP;',
'',
unistr('    -- Opcjonalnie wyczy\015B\0107 tabel\0119 tymczasow\0105 po przetworzeniu danych'),
'    DELETE FROM basket_staging;',
'END;',
''))
