,p_attribute_01=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'    v_order_id NUMBER;',
'BEGIN',
unistr('    -- Obliczanie wynagrodze\0144 na podstawie danych z temp_orders i wages z tabeli roles'),
'    FOR rec IN (SELECT e.employee_id, TRUNC(t.order_date, ''MM'') AS salary_month, SUM(t.commission_order) AS total_commission, r.wage',
'                FROM temp_orders t',
'                JOIN employees e ON t.employee_id = e.employee_id',
'                JOIN roles r ON e.role_id = r.role_id',
'                GROUP BY e.employee_id, TRUNC(t.order_date, ''MM''), r.wage) LOOP',
unistr('        -- Sprawdzenie, czy istnieje ju\017C wpis dla danego pracownika i miesi\0105ca'),
'        DECLARE',
'            v_existing_salary_sum NUMBER;',
'        BEGIN',
'            SELECT salary_sum INTO v_existing_salary_sum',
'            FROM salaries',
'            WHERE employee_id = rec.employee_id AND salary_month = rec.salary_month',
'            FOR UPDATE OF salary_sum;',
'',
unistr('            -- Aktualizacja istniej\0105cego wpisu'),
'            UPDATE salaries',
'            SET salary_sum = salary_sum + rec.total_commission',
'            WHERE employee_id = rec.employee_id AND salary_month = rec.salary_month;',
'        EXCEPTION',
'            WHEN NO_DATA_FOUND THEN',
'                -- Wstawienie nowego wpisu',
'                INSERT INTO salaries (salary_id, employee_id, salary_month, salary_sum)',
'                VALUES (salaries_seq.NEXTVAL, rec.employee_id, rec.salary_month, rec.total_commission + rec.wage);',
'        END;',
'    END LOOP;',
'',
'    -- Przeniesienie danych z temp_orders do orders oraz order_items_temp do order_items',
'    FOR rec IN (SELECT * FROM temp_orders) LOOP',
'        -- Generowanie nowego order_id',
'        SELECT orders_seq.NEXTVAL INTO v_order_id FROM dual;',
'',
'        -- Wstawienie rekordu do tabeli ORDERS',
'        INSERT INTO Orders (order_id, order_date, employee_id, price_sum, commission_order)',
'        VALUES (v_order_id, TO_DATE(TO_CHAR(rec.order_date, ''DD-MM-YYYY''), ''DD-MM-YYYY''), rec.employee_id, rec.price_sum, rec.commission_order);',
'',
unistr('        -- Wstawienie rekord\00F3w do tabeli ORDER_ITEMS'),
'        INSERT INTO Order_Items (order_id, product_id, quantity, price)',
'        SELECT v_order_id, oi.product_id, oi.quantity, oi.price',
'        FROM order_items_temp oi',
'        WHERE oi.temp_order_id = rec.temp_order_id;',
'    END LOOP;',
'',
unistr('    -- Usuni\0119cie danych z tabel tymczasowych'),
'    DELETE FROM order_items_temp;',
'    DELETE FROM temp_orders;',
'END;'))
,p_attribute_05=>'PLSQL'
,p_wait_for_result=>'Y'
);
